mapper.select:
SELECT COMMENT_NO, COMMENT_GROUP_NO, COMMENT_GROUP_SQ, COMMENT_TITLETAB, COMMENT_NICKNAME, COMMENT_CONTENT, RENT_BOARD_NO, FREE_BOARD_NO, CAR_BOARD_NO, COMMENT_REGDATE FROM DONCAR_COMMENT
FREE_BOARD_NO = #{free_board_no}
RENT_BOARD_NO = #{rent_board_no}
CAR_BOARD_NO = #{car_board_no}
ORDER BY COMMENT_GROUP_NO DESC, COMMENT_GROUP_SQ
