mapper.select:
SELECT MEMBER_ID
FROM DONCAR_MEMBER
WHERE MEMBER_ID = #{id}

SELECT MEMBER_NICKNAME
FROM DONCAR_MEMBER
WHERE MEMBER_NICKNAME = #{nickname}

mapper.update:
UPDATE DONCAR_MEMBER 
SET	MEMBER_EMAIL = #{member_email}
WHERE MEMBER_NO = #{member_no}


UPDATE DONCAR_MEMBER SET MEMBER_NICKNAME = #{member_nickname}, MEMBER_GENDER = #{member_gender}, MEMBER_BIRTH = #{member_birth},
MEMBER_ADDR = #{member_addr}, MEMBER_EMAIL = #{member_email}, MEMBER_PHONE = #{member_phone}, MEMBER_DRIVEACC = #{member_driveacc}
WHERE MEMBER_NO = #{member_no}
